#include <iostream>
#include <string>
#include <vector>
#include <sstream>

using namespace std;

class Solution {
public:
    typedef string::size_type s_t;
    string shortestPalindrome(string s) {
        if (s.size() < 2)
            return s;
        return string(s.rbegin(), s.rbegin()+s.size()-getLongestCommon(s)) + s;
    }
    s_t getLongestCommon(string s) {
        string rev(s.rbegin(), s.rend());
        ostringstream os;
        os << s << "#" << rev;
        string t = os.str();
        vector<int> common(t.size(), 0);
        for (s_t i = 1, iend = t.size(); i < iend; ++i) {
            s_t j = common[i-1];
            while (j > 0 && t[i] != t[j])
                j = common[j-1];
            common[i] = j ? j+1 : (t[i] == t[0] ? 1 : 0);
        }
        return common.back();
    }
};

int main(void)
{
    cout << Solution().shortestPalindrome("aacecaaa") << endl;
    cout << Solution().shortestPalindrome("abcd") << endl;
    cout << Solution().shortestPalindrome("") << endl;
    return 0;
}
